import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function View(){
    const[data,setdata]=useState([])
    const{id}=useParams(); //UseParams use for get specific parameter and use them.
    console.log(id)

    async function getsepratedata(){
        try{
            await axios.get(`http://localhost:4444/student/${id}`).then((naam)=>{
                setdata(naam.data)
            })
        }
        catch(error){
            console.log(error)
        }
    }

    useEffect(()=>{
        getsepratedata();
    },[])
    return(
        <>
        <h3>This is Seprately view data...</h3>
        <table>
            <tr>
                <th>ID</th>
                <th>Student Name</th>
                <th>Father Name</th>
            </tr>
            {data.map((seprate)=>{
                return <tr>
                    <td>{seprate.id}</td>
                    <td>{seprate["Student Name"]}</td>
                    <td>{seprate["Father Name"]}</td>
                </tr>
            })}
        </table>
        </>
    )
}
export default View;
