import axios from "axios"
import { useState } from "react"
import { useNavigate, useParams } from "react-router-dom"

function Edit() {
    const backlocation = useNavigate()

    const{id}=useParams

    const[newdata,setnewdata]=useState(axios.get(`http://localhost:4444/student/${id}`))

    function changefunc(e){
        setnewdata({
            [e.target.name]:e.target.value
         })
    }
    async function update(){
        await axios.putForm(`http://localhost:4444/student/${id}`)
    }

    function back(){
        backlocation('/List')
    }

    return (
        <>
            <h3>This is Edit the data....</h3>

            <form>
                <label>New Student Name : </label>
                <input type={"text"} name="studentname" onChange={(e)=>changefunc(e)}>{newdata['Student Name']}</input>
                <br/>
                <label>New Student Father Name : </label>
                <input type={"text"} name="fathername" onChange={(e)=>changefunc(e)}>{newdata['Father Name']}</input>
                <br/>
               <p><button onClick={()=>back()}>GO BACK</button>&nbsp;&nbsp;<button onClick={()=>update()}>UPDATE</button></p>
            </form>
        </>
    )
}
export default Edit
